import React, { Component } from "react";
import "../styles/index.css";
import "../styles/signUp.css";
import loginBackground from "../imgs/loginBackground.jpg";
import { Link } from "react-router-dom";
import axios from "axios";

class Signup extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      username: "",
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleInputChange(event) {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value,
    });
  }
  handleSubmit(event) {
    axios({
      method: "post",
      url: "/api",
      data: this.state,
      config: { headers: { "Content-Type": "multipart/form-data" } },
    })
      .then(function (response) {
        //handle success
      })
      .catch(function (response) {
        //handle error
      });

    event.preventDefault();
  }
  render() {
    return (
      <>
        <div class="layoutSignup">
          <div class="form-ele1">
            <h1>Sign Up </h1>
            <div class="form-ele2" id="signUpform">
              <form method="POST" action="/api">
                <label>User Name</label>
                <br />
                <div class="form-style">
                  <input
                    class=" h-12"
                    type="text"
                    name="username"
                    value={this.state.username}
                    onChange={this.handleInputChange}
                  />
                </div>
                <br />
                <label>Password</label>
                <br />
                <div class="form-style">
                  <input class=" h-12" type="password" name="pw" />
                </div>
                <br />
                <label>Re-password</label>
                <br />
                <div class="form-style">
                  <input class=" h-12" type="password" name="pwre" />
                </div>
                <br />
                <label>Email</label>
                <br />
                <div class="form-style">
                  <input
                    class=" h-12"
                    type="email"
                    name="email"
                    value={this.state.email}
                    onChange={this.handleInputChange}
                  />
                </div>
                <br />
                <div id="link">
                  <Link to="/login">Login instead </Link>
                </div>
                <button class="w-32 h-16 formButton" type="submit">
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
        <img src={loginBackground} />
      </>
    );
  }
}

export default Signup;
